import logging
import time


logger = logging.getLogger()
logger.setLevel(logging.INFO)


def describe_all_dax_clusters(dax_client, page_size: int = 25, interval_between_calls: float = 0.05):
    resources: list[dict] = []
    paginator = dax_client.get_paginator("describe_clusters")
    page_iterator = paginator.paginate(PaginationConfig={"PageSize": page_size})
    for page in page_iterator:
        resources.extend(page.get("Clusters", []))
        time.sleep(interval_between_calls)
    return resources
