import logging
import time


logger = logging.getLogger()
logger.setLevel(logging.INFO)


def list_all_dynamo_db_tables(dynamo_db_client, page_size: int = 25, interval_between_calls: float = 0.05):
    resources: list[dict] = []
    paginator = dynamo_db_client.get_paginator("list_tables")
    page_iterator = paginator.paginate(Limit=page_size)
    for page in page_iterator:
        resources.extend(page.get("TableNames", []))
        time.sleep(interval_between_calls)
    return resources
