import logging
import time


logger = logging.getLogger()
logger.setLevel(logging.INFO)


def describe_all_efs_file_systems(efs_client, page_size: int = 25, interval_between_calls: float = 0.05):
    resources: list[dict] = []
    paginator = efs_client.get_paginator("describe_file_systems")
    page_iterator = paginator.paginate(PaginationConfig={"PageSize": page_size})
    for page in page_iterator:
        resources.extend(page.get("FileSystems", []))
        time.sleep(interval_between_calls)
    return resources
