import logging
import time


logger = logging.getLogger()
logger.setLevel(logging.INFO)


def list_all_eks_clusters(eks_client, page_size: int = 25, interval_between_calls: float = 0.05):
    resources: list[dict] = []
    paginator = eks_client.get_paginator("list_clusters")
    page_iterator = paginator.paginate(maxResults=page_size)
    for page in page_iterator:
        resources.extend(page.get("clusters", []))
        time.sleep(interval_between_calls)
    return resources
