import logging
import time

import botocore.exceptions

logger = logging.getLogger()
logger.setLevel(logging.INFO)


def list_all_event_bridge_rules(event_bridge_client, interval_between_calls: float = 0.1) -> list[dict]:
    try:
        args = {}
        resources = []

        while True:
            response = event_bridge_client.list_rules(**args)

            if response:
                resources.extend(response.get("Rules", []))
                args["NextToken"] = response.get("NextToken")
            else:
                args["NextToken"] = None

            if not args.get("NextToken"):
                break
            else:
                time.sleep(interval_between_calls)

        return resources

    except botocore.exceptions.ClientError as ex:
        if "ResourceNotFound" in ex.response["Error"]["Code"]:
            ex.response["Error"]["Message"] = "Failed to list rules for EventBridge. Resource not found."
        else:
            ex.response["Error"]["Message"] = "InternalError"
            ex.response["Error"]["Code"] = "InternalError"
        raise ex


def list_all_event_bridge_rule_targets(
    event_bridge_client, rule_name: str, interval_between_calls: float = 0.1
) -> list[dict]:
    try:
        args = {"Rule": rule_name}
        resources = []

        while True:
            response = event_bridge_client.list_targets_by_rule(**args)

            if response:
                resources.extend(response.get("Targets", []))
                args["NextToken"] = response.get("NextToken")
            else:
                args["NextToken"] = None

            if not args.get("NextToken"):
                break
            else:
                time.sleep(interval_between_calls)

        return resources

    except botocore.exceptions.ClientError as ex:
        if "NotFound" in ex.response["Error"]["Code"]:
            ex.response["Error"]["Message"] = "Failed to fetch all targets for a rule. Resource not found."
        else:
            ex.response["Error"]["Message"] = "InternalError"
            ex.response["Error"]["Code"] = "InternalError"
        raise ex
