import logging
import time


logger = logging.getLogger()
logger.setLevel(logging.INFO)


def list_all_kinesis_streams(kinesis_client, page_size: int = 25, interval_between_calls: float = 0.05):
    resources: list[dict] = []
    paginator = kinesis_client.get_paginator("list_streams")
    page_iterator = paginator.paginate(Limit=page_size)
    for page in page_iterator:
        resources.extend(page.get("StreamNames", []))
        time.sleep(interval_between_calls)
    return resources
